public class StorelinkTest31 {

	public static void main(String[] args) {

//		for(int i = 1000; i < 10000; i++) {
//			int n = 50000000;
		String s = "";

		int sol = sol(s);

		System.out.println();
		System.out.println(sol);
//			System.out.println(i + " = " + sol);
//		}

//		System.out.println(Arrays.toString(sol));
	}

	private static int sol(String s) {

		String[] split = s.split("");
		int max = 0;
		for (int i = 1; i < split.length - 1; i++) {
			int left = i - 1, right = i + 1, count = 0;

			System.out.println(i + " start");
			while (left >= 0 && right <= split.length - 1) {
				String leftstr = split[left];
				String rightstr = split[right];

				System.out.println(leftstr + " / " + rightstr + " => " + leftstr.equals(rightstr));
				
				if (leftstr.equals(rightstr)) {
					count++;
				}
				left--;
				right++;
			}
			System.out.println(i + " end => " + (count * 2 + 1));
			System.out.println();
			max = Math.max(max, count * 2 + 1);
		}

		return max;
	}
}
